import React, {Component} from 'react';
import {
  SwipeableDrawer,
  List,
  Divider,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  Box,
  Grid
} from '@material-ui/core';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import MenuIcon from '@material-ui/icons/Menu';
import ExitIcon from '@material-ui/icons/ExitToApp';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {updateAction} from './../actions/updateAction';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      drawerOpen: false
    }
    //this.user = this.props.currentUser;
    //this.component = this.props.component;
  }

  toggleDrawer = (drawerOpen) => event => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    this.setState({drawerOpen});
  };

  handleListItemClick = (name) => new Promise(resolve => {
    setTimeout(() => {
      resolve();
      this
        .props
        .history
        .push(name);
    }, 200)
  });

  logOut = () => {
    localStorage.setItem('isAuthed', false);
    this.props.history.push('/');
  }

  componentWillMount() {
    const isAuthed = localStorage.getItem('isAuthed');
    if (!isAuthed || isAuthed === 'false') {
      this
        .props
        .history
        .push('/')
    }
  }

  sideList = () => (
    <div
      className="list"
      role="presentation"
      onClick={this.toggleDrawer(false)}
      onKeyDown={this.toggleDrawer(false)}>
      <Divider/>
      <List>
        <ListItem button key="Table" onClick={() => this.handleListItemClick('/table')}>
          <ListItemIcon>
            <InboxIcon/>
          </ListItemIcon>
          <ListItemText primary="Table"/>
        </ListItem>
      </List>
      <Divider/>
      <List>
        <ListItem button key="Log Out" onClick={this.logOut}>
          <ListItemIcon>
            <ExitIcon/>
          </ListItemIcon>
          <ListItemText primary="Log Out"/>
        </ListItem>

      </List>
    </div>
  );

  render() {
    return (
      <div>
        <Grid container direction="row">
          <Box ml={2} mt={1}>
            <IconButton
              color="default"
              aria-label="Open drawer"
              onClick={this.toggleDrawer(true)}
              edge="start">
              <MenuIcon/>
            </IconButton>
          </Box>
          {this.component
            ? this.component
            : null}
        </Grid>

        <SwipeableDrawer
          open={this.state.drawerOpen}
          onClose={this.toggleDrawer(false)}
          onOpen={this.toggleDrawer(true)}>
          {this.sideList('left')}
        </SwipeableDrawer>
      </div>
    );
  }
}

